import React, { useState, useEffect } from 'react';
import './App.css'; // Mantenha seu CSS

// --- Configura√ß√µes e Constantes ---
const MASTER_PASSWORD = 'adminOdontoJus2024';

const DAYS_OF_WEEK = [
  "Segunda-feira", "Ter√ßa-feira", "Quarta-feira", "Quinta-feira", "Sexta-feira", "S√°bado"
];

const EQUIPMENT_LIST = [
  "Lentes de Contato", "Harmoniza√ß√£o Facial", "Tratamentos com Oz√¥nio", "Clareamento Dental", "Implantes Dent√°rios", "Hidrolifting", "Microagulhamento", "Lipoenxertia Facial", "Scanner e CAD/CAM", "Outro (Consulta/Avalia√ß√£o)"
];

// NOVO: Adicionamos os turnos
const TURNOS = ["Manh√£", "Tarde", "Noite"];

function App() {
  // --- Estados da Aplica√ß√£o ---
  const [appointments, setAppointments] = useState(() => {
    const savedAppointments = localStorage.getItem('appointments');
    return savedAppointments ? JSON.parse(savedAppointments) : [];
  });

  // Estado do formul√°rio atualizado para incluir o turno
  const [newAppointment, setNewAppointment] = useState({
    patient: '',
    day: DAYS_OF_WEEK[0],
    turno: TURNOS[0], // NOVO: Valor inicial para o dropdown de turnos
    equipment: EQUIPMENT_LIST[0],
    password: ''
  });

  // --- Efeitos ---
  useEffect(() => {
    localStorage.setItem('appointments', JSON.stringify(appointments));
  }, [appointments]);

  // --- Manipuladores de Eventos (Handlers) ---
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewAppointment(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleAddAppointment = (e) => {
    e.preventDefault();
    if (!newAppointment.patient.trim() || !newAppointment.password.trim()) {
      alert('Por favor, preencha o nome do paciente e defina uma senha para o agendamento.');
      return;
    }
    
    // NOVO: Valida√ß√£o para impedir agendamento em hor√°rio j√° ocupado
    const isSlotTaken = appointments.some(
      app => app.day === newAppointment.day && app.turno === newAppointment.turno
    );

    if (isSlotTaken) {
      alert('Este hor√°rio j√° est√° agendado! Por favor, escolha outro dia ou turno.');
      return;
    }

    const appointmentToAdd = {
      id: Date.now(),
      ...newAppointment
    };

    setAppointments([...appointments, appointmentToAdd]);

    // Limpa o formul√°rio
    setNewAppointment({
      patient: '',
      day: DAYS_OF_WEEK[0],
      turno: TURNOS[0],
      equipment: EQUIPMENT_LIST[0],
      password: ''
    });
  };

  const handleRemoveAppointment = (id) => {
    const appointmentToRemove = appointments.find(app => app.id === id);
    if (!appointmentToRemove) return;

    const enteredPassword = prompt(
      `Para desmarcar o agendamento de "${appointmentToRemove.patient}", digite a senha do agendamento ou a senha de administrador:`
    );

    if (enteredPassword === null) {
      return;
    }
    
    if (enteredPassword === appointmentToRemove.password || enteredPassword === MASTER_PASSWORD) {
      const updatedAppointments = appointments.filter(app => app.id !== id);
      setAppointments(updatedAppointments);
      alert('Agendamento desmarcado com sucesso!');
    } else {
      alert('Senha incorreta! N√£o foi poss√≠vel desmarcar o agendamento.');
    }
  };
  
  const handleResetAll = () => {
    const enteredPassword = prompt(
      'ATEN√á√ÉO: Voc√™ est√° prestes a apagar TODOS os agendamentos.\n\nDigite a senha de ADMINISTRADOR para confirmar:'
    );

    if (enteredPassword === MASTER_PASSWORD) {
      if (window.confirm('Tem certeza que deseja apagar TUDO? Esta a√ß√£o n√£o pode ser desfeita.')) {
        setAppointments([]);
        alert('Todos os agendamentos foram removidos.');
      }
    } else if (enteredPassword !== null) {
      alert('Senha de administrador incorreta!');
    }
  };

  // --- Renderiza√ß√£o do Componente ---
  return (
    <div className="App">
      <header className="App-header">
        <h1>üóìÔ∏è Agenda Odonto Jus - Dra. Neta Sampaio</h1>
      </header>
      <main>
        {/* NOVO: Tabela de Hor√°rios */}
        <div className="schedule-table-container">
          <h2>Hor√°rios da Semana</h2>
          <table>
            <thead>
              <tr>
                <th>Turno</th>
                {DAYS_OF_WEEK.map(day => <th key={day}>{day}</th>)}
              </tr>
            </thead>
            <tbody>
              {TURNOS.map(turno => (
                <tr key={turno}>
                  <td>{turno}</td>
                  {DAYS_OF_WEEK.map(day => {
                    const appointment = appointments.find(app => app.day === day && app.turno === turno);
                    return (
                      <td key={day} className={appointment ? 'booked' : 'available'}>
                        {appointment ? appointment.patient : 'Livre'}
                      </td>
                    );
                  })}
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        <div className="content-wrapper">
            <div className="form-container">
            <h2>Novo Agendamento</h2>
            <form onSubmit={handleAddAppointment}>
                <input
                type="text"
                name="patient"
                value={newAppointment.patient}
                onChange={handleInputChange}
                placeholder="Nome do Paciente"
                required
                />
                <select name="day" value={newAppointment.day} onChange={handleInputChange}>
                {DAYS_OF_WEEK.map(day => (
                    <option key={day} value={day}>{day}</option>
                ))}
                </select>
                {/* NOVO: Select de Turnos */}
                <select name="turno" value={newAppointment.turno} onChange={handleInputChange}>
                {TURNOS.map(turno => (
                    <option key={turno} value={turno}>{turno}</option>
                ))}
                </select>
                <select name="equipment" value={newAppointment.equipment} onChange={handleInputChange}>
                {EQUIPMENT_LIST.map(equip => (
                    <option key={equip} value={equip}>{equip}</option>
                ))}
                </select>
                <input
                type="password"
                name="password"
                value={newAppointment.password}
                onChange={handleInputChange}
                placeholder="Crie uma senha para o agendamento"
                required
                />
                <button type="submit">Agendar</button>
            </form>
            </div>

            <div className="appointments-list">
            <h2>Agendamentos Confirmados</h2>
            {appointments.length === 0 ? (
                <p>Nenhum agendamento para hoje.</p>
            ) : (
                <ul>
                {appointments.map((app) => (
                    <li key={app.id}>
                    <div className="appointment-details">
                        <strong>{app.patient}</strong>
                        <span>{app.day} ({app.turno}) - {app.equipment}</span>
                    </div>
                    <button onClick={() => handleRemoveAppointment(app.id)} className="remove-btn">
                        Desmarcar
                    </button>
                    </li>
                ))}
                </ul>
            )}
            </div>
        </div>
        
        <div className="admin-section">
           <button onClick={handleResetAll} className="reset-all-btn">
             Resetar Todos os Agendamentos
           </button>
        </div>
      </main>
    </div>
  );
}

export default App;
