import React, { useState, useEffect, useCallback } from 'react';

// --- CONFIGURAÇÕES GERAIS ---
const LOCAL_STORAGE_KEY = 'labBookingsV2'; // Nova chave para evitar conflitos com a versão antiga
const MASTER_PASSWORD = 'admin123'; // Senha mestra para administrador

// --- COMPONENTE PRINCIPAL ---
function App() {
  // --- ESTADOS (DATA) ---
  const [equipment] = useState([
    "Microscópio Óptico", "Centrífuga Refrigerada", "Espectrofotômetro",
    "Balança Analítica", "pHmetro de Bancada", "Autoclave",
    "Estufa de Cultura", "Capela de Exaustão"
  ]);
  const [days] = useState(["Seg", "Ter", "Qua", "Qui", "Sex"]);
  const [shifts] = useState(["Manhã", "Tarde", "Noite"]);

  // Estado dos agendamentos: { "idSlot": { studentName: "Nome", password: "senha" } }
  const [bookings, setBookings] = useState({});

  // --- ESTADOS (UI) ---
  const [selectedEquipment, setSelectedEquipment] = useState(equipment[0]);
  const [selectedDay, setSelectedDay] = useState(days[0]);
  const [modal, setModal] = useState({ type: null, data: null, message: '' });

  // --- HOOKS DE EFEITO (localStorage) ---
  // Carregar dados na inicialização
  useEffect(() => {
    try {
      const storedBookings = localStorage.getItem(LOCAL_STORAGE_KEY);
      if (storedBookings) setBookings(JSON.parse(storedBookings));
    } catch (error) {
      console.error("Erro ao carregar dados:", error);
      setModal({ type: 'info', message: 'Houve um erro ao carregar os agendamentos salvos.' });
    }
  }, []);

  // Salvar dados quando 'bookings' mudar
  useEffect(() => {
    try {
      localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(bookings));
    } catch (error) {
      console.error("Erro ao salvar dados:", error);
      setModal({ type: 'info', message: 'Não foi possível salvar o último agendamento.' });
    }
  }, [bookings]);

  // --- FUNÇÕES DE LÓGICA DE NEGÓCIO ---

  // Inicia o fluxo de agendamento ou desmarcação
  const handleSlotClick = (shift) => {
    const timeSlotId = `${selectedEquipment}-${selectedDay}-${shift}`;
    const existingBooking = bookings[timeSlotId];

    if (existingBooking) {
      // Se já existe, abre o modal de desmarcar
      setModal({ type: 'unbookConfirm', data: { timeSlotId, booking: existingBooking } });
    } else {
      // Se está livre, abre o modal de agendar
      setModal({ type: 'bookInput', data: { shift } });
    }
  };

  // Confirma um novo agendamento
  const performBooking = (studentName, password) => {
    if (!studentName?.trim() || !password?.trim()) {
      setModal({ type: 'info', message: 'Nome e senha são obrigatórios.' });
      return;
    }
    const { shift } = modal.data;
    const timeSlotId = `${selectedEquipment}-${selectedDay}-${shift}`;
    const newBookingData = { studentName: studentName.trim(), password: password.trim() };
    
    setBookings(prev => ({ ...prev, [timeSlotId]: newBookingData }));
    setModal({ type: 'info', message: 'Agendamento realizado com sucesso!' });
  };

  // Tenta desmarcar um agendamento
  const performUnbooking = (passwordAttempt) => {
    const { timeSlotId, booking } = modal.data;
    if (passwordAttempt === booking.password || passwordAttempt === MASTER_PASSWORD) {
      const newBookings = { ...bookings };
      delete newBookings[timeSlotId];
      setBookings(newBookings);
      setModal({ type: 'info', message: 'Agendamento removido com sucesso.' });
    } else {
      setModal({ type: 'info', message: 'Senha incorreta. Apenas quem agendou ou um administrador pode desmarcar.' });
    }
  };

  // Inicia o fluxo de reset total
  const requestResetConfirmation = () => {
    setModal({ type: 'resetConfirm' });
  };
  
  // Executa o reset se a senha mestra estiver correta
  const performReset = useCallback((passwordAttempt) => {
    if (passwordAttempt === MASTER_PASSWORD) {
      setBookings({});
      setModal({ type: 'info', message: 'Todos os agendamentos foram resetados com sucesso!' });
    } else {
      setModal({ type: 'info', message: 'Senha de administrador incorreta. A operação foi cancelada.' });
    }
  }, []); // useCallback para garantir que a função não seja recriada desnecessariamente

  const closeModal = () => setModal({ type: null, data: null, message: '' });

  // --- COMPONENTES DE UI E RENDERIZAÇÃO ---

  const ModalRenderer = () => {
    if (!modal.type) return null;

    const baseModalClass = "fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50 p-4";
    const modalContentClass = "bg-white rounded-xl shadow-2xl p-6 w-full max-w-md text-center transform transition-all";
    
    // Variáveis locais para inputs, como solicitado
    let nameInput, passInput, passAttemptInput = '';

    switch (modal.type) {
      case 'info': return (
        <div className={baseModalClass} onClick={closeModal}>
          <div className={modalContentClass} onClick={e => e.stopPropagation()}>
            <h3 className="text-xl font-bold text-gray-800 mb-4">Aviso</h3>
            <p className="text-gray-600 mb-6">{modal.message}</p>
            <button onClick={closeModal} className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-6 rounded-lg">OK</button>
          </div>
        </div>
      );

      case 'bookInput': return (
        <div className={baseModalClass}>
          <div className={modalContentClass}>
            <h3 className="text-xl font-bold text-gray-800 mb-2">Agendar Horário</h3>
            <p className="text-gray-600 mb-4">
              Agendando <span className="font-semibold">{selectedEquipment}</span> para <span className="font-semibold">{selectedDay}, {modal.data.shift}</span>.
            </p>
            <input type="text" placeholder="Seu nome completo" autoFocus onChange={e => nameInput = e.target.value} className="w-full p-3 border rounded-lg mb-3" />
            <input type="password" placeholder="Crie uma senha para desmarcar" onChange={e => passInput = e.target.value} className="w-full p-3 border rounded-lg mb-6" />
            <div className="flex justify-center gap-4">
              <button onClick={() => performBooking(nameInput, passInput)} className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-6 rounded-lg">Confirmar</button>
              <button onClick={closeModal} className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-6 rounded-lg">Cancelar</button>
            </div>
          </div>
        </div>
      );
      
      case 'unbookConfirm': return (
        <div className={baseModalClass}>
          <div className={modalContentClass}>
            <h3 className="text-xl font-bold text-gray-800 mb-2">Desmarcar Horário</h3>
            <p className="text-gray-600 mb-4">
              Para desmarcar o horário de <span className="font-semibold">{modal.data.booking.studentName}</span>, digite a senha do agendamento ou a senha mestre.
            </p>
            <input type="password" placeholder="Digite a senha" autoFocus onChange={e => passAttemptInput = e.target.value} className="w-full p-3 border rounded-lg mb-6" />
            <div className="flex justify-center gap-4">
              <button onClick={() => performUnbooking(passAttemptInput)} className="bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-6 rounded-lg">Desmarcar</button>
              <button onClick={closeModal} className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-6 rounded-lg">Cancelar</button>
            </div>
          </div>
        </div>
      );
      
      case 'resetConfirm': return (
        <div className={baseModalClass}>
          <div className={modalContentClass}>
            <h3 className="text-xl font-bold text-red-600 mb-2">Resetar Todos Agendamentos</h3>
            <p className="text-gray-600 mb-4">Esta ação é irreversível. Por favor, digite a senha de administrador para continuar.</p>
            <input type="password" placeholder="Senha de Administrador" autoFocus onChange={e => passAttemptInput = e.target.value} className="w-full p-3 border rounded-lg mb-6" />
            <div className="flex justify-center gap-4">
              <button onClick={() => performReset(passAttemptInput)} className="bg-red-600 hover:bg-red-700 text-white font-bold py-2 px-6 rounded-lg">Resetar Tudo</button>
              <button onClick={closeModal} className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-6 rounded-lg">Cancelar</button>
            </div>
          </div>
        </div>
      );

      default: return null;
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-4 sm:p-6 lg:p-8 font-inter">
      <ModalRenderer />
      
      <header className="w-full max-w-4xl mb-8 text-center">
        <h1 className="text-3xl sm:text-4xl lg:text-5xl font-extrabold text-gray-800">Sistema de Agendamento</h1>
        <p className="text-gray-500 mt-2">Laboratório de Ciências</p>
      </header>

      <main className="w-full max-w-4xl bg-white shadow-xl rounded-lg p-6 space-y-6">
        {/* -- Seção de Filtros com Dropdowns -- */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label htmlFor="equipment-select" className="block text-sm font-medium text-gray-700 mb-1">Equipamento:</label>
            <select id="equipment-select" value={selectedEquipment} onChange={e => setSelectedEquipment(e.target.value)} className="w-full p-3 border border-gray-300 rounded-lg bg-white shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500">
              {equipment.map(e => <option key={e} value={e}>{e}</option>)}
            </select>
          </div>
          <div>
            <label htmlFor="day-select" className="block text-sm font-medium text-gray-700 mb-1">Dia da Semana:</label>
            <select id="day-select" value={selectedDay} onChange={e => setSelectedDay(e.target.value)} className="w-full p-3 border border-gray-300 rounded-lg bg-white shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500">
              {days.map(d => <option key={d} value={d}>{d}</option>)}
            </select>
          </div>
        </div>

        {/* -- Lista de Turnos para o dia/equipamento selecionado -- */}
        <div className="border-t border-gray-200 pt-6">
          <h2 className="text-xl font-bold text-gray-800 mb-4">Horários para {selectedDay}</h2>
          <ul className="space-y-3">
            {shifts.map(shift => {
              const timeSlotId = `${selectedEquipment}-${selectedDay}-${shift}`;
              const bookingInfo = bookings[timeSlotId];
              const isBooked = !!bookingInfo;

              return (
                <li key={shift} className="flex items-center justify-between bg-gray-50 p-4 rounded-lg shadow-sm">
                  <div className="flex flex-col">
                     <span className="font-semibold text-gray-800">{shift}</span>
                     {isBooked && <span className="text-sm text-gray-500">Agendado por: {bookingInfo.studentName}</span>}
                  </div>
                  <button
                    onClick={() => handleSlotClick(shift)}
                    className={`px-4 py-2 rounded-lg text-sm font-bold transition-transform transform hover:scale-105
                      ${isBooked
                        ? 'bg-yellow-400 hover:bg-yellow-500 text-yellow-900'
                        : 'bg-green-500 hover:bg-green-600 text-white'
                      }`}
                  >
                    {isBooked ? 'Desmarcar' : 'Agendar'}
                  </button>
                </li>
              );
            })}
          </ul>
        </div>
      </main>

      <footer className="mt-8">
        <button
          onClick={requestResetConfirmation}
          className="bg-red-600 hover:bg-red-700 text-white font-bold py-2 px-5 rounded-lg shadow-md hover:shadow-lg"
        >
          Resetar Todos os Agendamentos
        </button>
      </footer>
    </div>
  );
}

export default App;
