import React, { useState, useEffect, useCallback } from 'react';

// Chave para salvar/carregar dados do localStorage
const LOCAL_STORAGE_KEY = 'labBookings';

// Componente principal do aplicativo
function App() {
  // Dados estáticos da aplicação
  const [equipment] = useState([
    "Microscópio Óptico",
    "Centrífuga Refrigerada",
    "Espectrofotômetro",
    "Balança Analítica",
    "pHmetro de Bancada",
    "Autoclave",
    "Estufa de Cultura",
    "Capela de Exaustão"
  ]);

  const [days] = useState(["Seg", "Ter", "Qua", "Qui", "Sex"]);
  const [shifts] = useState(["Manhã", "Tarde", "Noite"]);

  // Estado para os agendamentos
  const [bookings, setBookings] = useState({});

  // Estados para controlar os modais
  const [modal, setModal] = useState({ type: null, data: null, message: '' });

  // ===== EFEITOS (Hooks para localStorage) =====

  // Efeito para CARREGAR os dados do localStorage na inicialização
  useEffect(() => {
    try {
      const storedBookings = localStorage.getItem(LOCAL_STORAGE_KEY);
      if (storedBookings) {
        setBookings(JSON.parse(storedBookings));
      }
    } catch (error) {
      console.error("Erro ao carregar dados do localStorage:", error);
      // Opcional: mostrar um aviso para o usuário
    }
  }, []);

  // Efeito para SALVAR os dados no localStorage sempre que `bookings` mudar
  useEffect(() => {
    try {
      localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(bookings));
    } catch (error) {
      console.error("Erro ao salvar dados no localStorage:", error);
      // Opcional: mostrar um aviso para o usuário
    }
  }, [bookings]);

  // ===== FUNÇÕES DE MANIPULAÇÃO DE AGENDAMENTO =====

  const handleSlotClick = (equipName, day, shift) => {
    const timeSlotId = `${equipName}-${day}-${shift}`;
    const existingBooking = bookings[timeSlotId];

    if (existingBooking) {
      setModal({ type: 'info', message: `Este horário já foi agendado por ${existingBooking}.` });
    } else {
      setModal({ type: 'nameInput', data: { equipName, day, shift } });
    }
  };

  const confirmBooking = (studentName) => {
    if (!studentName || !studentName.trim()) {
      setModal({ type: 'info', message: 'O nome é obrigatório para o agendamento.' });
      return;
    }
    const { equipName, day, shift } = modal.data;
    const timeSlotId = `${equipName}-${day}-${shift}`;
    const newBookings = { ...bookings, [timeSlotId]: studentName.trim() };
    setBookings(newBookings);
    closeModal();
  };

  const requestResetConfirmation = () => {
    setModal({ type: 'confirmReset', message: 'Você tem certeza que deseja apagar TODOS os agendamentos? Esta ação não pode ser desfeita.' });
  };

  const performReset = useCallback(() => {
    setBookings({});
    setModal({ type: 'info', message: 'Todos os agendamentos foram resetados.' });
  }, []);

  const closeModal = () => {
    setModal({ type: null, data: null, message: '' });
  };

  // ===== COMPONENTES DE MODAL =====

  const ModalRenderer = () => {
    if (!modal.type) return null;

    const baseModalClass = "fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50 p-4";
    const modalContentClass = "bg-white rounded-xl shadow-2xl p-6 w-full max-w-md text-center transform transition-all";

    switch (modal.type) {
      case 'info':
        return (
          <div className={baseModalClass} onClick={closeModal}>
            <div className={modalContentClass} onClick={(e) => e.stopPropagation()}>
              <h3 className="text-xl font-bold text-gray-800 mb-4">Aviso</h3>
              <p className="text-gray-600 mb-6">{modal.message}</p>
              <button onClick={closeModal} className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-6 rounded-lg transition-transform transform hover:scale-105">
                OK
              </button>
            </div>
          </div>
        );

      case 'nameInput':
        let studentName = ''; // Variável local para o input
        const { equipName, day, shift } = modal.data;
        return (
          <div className={baseModalClass}>
            <div className={modalContentClass}>
              <h3 className="text-xl font-bold text-gray-800 mb-2">Confirmar Agendamento</h3>
              <p className="text-gray-600 mb-4">Agendando <span className="font-semibold">{equipName}</span> para <span className="font-semibold">{day}, {shift}</span>.</p>
              <input
                type="text"
                placeholder="Digite seu nome completo"
                autoFocus
                onChange={(e) => (studentName = e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && confirmBooking(studentName)}
                className="w-full p-3 border border-gray-300 rounded-lg mb-6 focus:outline-none focus:ring-2 focus:ring-blue-400"
              />
              <div className="flex justify-center gap-4">
                <button onClick={() => confirmBooking(studentName)} className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-6 rounded-lg transition-transform transform hover:scale-105">
                  Confirmar
                </button>
                <button onClick={closeModal} className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-6 rounded-lg transition-transform transform hover:scale-105">
                  Cancelar
                </button>
              </div>
            </div>
          </div>
        );

      case 'confirmReset':
        return (
          <div className={baseModalClass}>
            <div className={modalContentClass}>
              <h3 className="text-xl font-bold text-red-600 mb-4">Confirmação Necessária</h3>
              <p className="text-gray-600 mb-6">{modal.message}</p>
              <div className="flex justify-center gap-4">
                <button onClick={performReset} className="bg-red-600 hover:bg-red-700 text-white font-bold py-2 px-6 rounded-lg transition-transform transform hover:scale-105">
                  Sim, Apagar Tudo
                </button>
                <button onClick={closeModal} className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-6 rounded-lg transition-transform transform hover:scale-105">
                  Cancelar
                </button>
              </div>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  // ===== RENDERIZAÇÃO PRINCIPAL =====

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center p-4 sm:p-6 lg:p-8 font-inter">
      <header className="w-full max-w-7xl mb-8">
        <h1 className="text-3xl sm:text-4xl lg:text-5xl font-extrabold text-gray-800 text-center">
          Sistema de Agendamento
        </h1>
        <p className="text-center text-gray-500 mt-2">Laboratório de Ciências</p>
      </header>

      <main className="w-full max-w-7xl bg-white shadow-xl rounded-lg overflow-hidden">
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th scope="col" className="sticky left-0 bg-gray-50 px-6 py-3 text-left text-xs font-bold text-gray-600 uppercase tracking-wider z-10">
                  Equipamento
                </th>
                {days.map(day => (
                  <th key={day} colSpan={shifts.length} className="px-6 py-3 text-center text-xs font-bold text-gray-600 uppercase tracking-wider border-l border-gray-200">
                    {day}
                  </th>
                ))}
              </tr>
              <tr>
                <th scope="col" className="sticky left-0 bg-gray-50 px-6 py-3 z-10"></th>
                {days.map(day =>
                  shifts.map(shift => (
                    <th key={`${day}-${shift}`} scope="col" className="px-1 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider border-l border-gray-200">
                      {shift.substring(0, 3)}
                    </th>
                  ))
                )}
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {equipment.map((equip, equipIndex) => (
                <tr key={equip} className={`${equipIndex % 2 === 0 ? 'bg-white' : 'bg-gray-50'}`}>
                  <td className="sticky left-0 bg-inherit px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900 z-10">
                    {equip}
                  </td>
                  {days.map(day =>
                    shifts.map(shift => {
                      const timeSlotId = `${equip}-${day}-${shift}`;
                      const bookedBy = bookings[timeSlotId];
                      return (
                        <td key={timeSlotId} className="px-2 py-2 whitespace-nowrap border-l border-gray-200">
                          <button
                            onClick={() => handleSlotClick(equip, day, shift)}
                            className={`w-full h-full p-2 rounded-md text-xs font-semibold text-center transition-all duration-200
                              ${bookedBy
                                ? 'bg-red-200 text-red-800 cursor-default shadow-sm'
                                : 'bg-green-100 text-green-800 hover:bg-green-200 hover:shadow-md hover:scale-105'
                              }`}
                          >
                            {bookedBy ? `✓ ${bookedBy.split(' ')[0]}` : 'Agendar'}
                          </button>
                        </td>
                      );
                    })
                  )}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </main>

      <footer className="mt-8">
        <button
          onClick={requestResetConfirmation}
          className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-5 rounded-lg shadow-md hover:shadow-lg transition-all transform hover:scale-105"
        >
          Resetar Todos os Agendamentos
        </button>
      </footer>

      <ModalRenderer />
    </div>
  );
}

export default App;
